<template>
  <view v-if="show" class="x-add-miniprogram" :style="overlayStyle">
    <view :style="triangleStyle"></view>
    <view :style="panelStyle">
      <view class="title">下次可以这样找到{{ name }}</view>
      <view class="setup setup1">
        <view>1.点击右上角“更多"</view>
        <image :src="dotImage"></image>
      </view>
      <view class="setup setup2">
        <view>2.点击“添加到我的小程序”</view>
        <image :src="addMiniprogramImage"></image>
      </view>
      <view class="setup setup3">3.微信首页下拉，即可快速进入</view>
      <view class="operat">
        <text class="ok" @click.stop="handleClose">我知道了</text>
      </view>
    </view>
  </view>
</template>

<script setup>
import { ref } from "vue";

const emit = defineEmits(["close"]);
const props = defineProps({
  name: {
    type: String,
    default: "uni-app",
  },
  zIndex: {
    type: [Number, String],
    default: 1001,
  },
  navigationStyle: {
    type: String,
    validator: function (value) {
      return ["default", "custom"].includes(value);
    },
    default: "default",
  },
});

const borderWidth = 10;
const { left, bottom, width } = uni.getMenuButtonBoundingClientRect();
const navigationHeight = props.navigationStyle === "default" ? 0 : bottom + 8;
const overlayStyle = {
  position: "fixed",
  top: 0,
  bottom: 0,
  left: 0,
  right: 0,
  zIndex: `${props.zIndex}`,
  fontSize: "16px",
  backgroundColor: "rgba(0, 0, 0, 0.3)",
  animation: "fadein .3s ease forwards",
};
const triangleStyle = {
  position: "absolute",
  top: `${navigationHeight - borderWidth}px`,
  left: `${left + parseInt(width / 2) - borderWidth}px`,
  border: `${borderWidth}px solid transparent`,
  borderBottom: `${borderWidth}px solid #fff`,
};
const panelStyle = {
  position: "absolute",
  top: `${navigationHeight + borderWidth}px`,
  right: "12px",
  width: "280px",
  height: "278px",
  padding: "16px",
  boxSizing: "border-box",
  borderRadius: "12px",
  color: "#303030",
  backgroundColor: "#fff",
};

const show = ref(false);
uni.checkIsAddedToMyMiniProgram({
  success: (res) => {
    show.value = !res.added;
  },
});
function handleClose() {
  show.value = false;
  emit("close");
}

const dotImage =
  "data:image/png;base64,";
const addMiniprogramImage =
  "data:image/png;base64,";
</script>

